// Setează data la care vrei să faci countdown (exemplu: 31 ianuarie 2025, ora 12:00)
var countDownDate = new Date("Jan 31, 2025 12:00:00").getTime();

// Actualizează countdown-ul la fiecare 1 secundă
var x = setInterval(function () {

    // Obține data și ora curente
    var now = new Date().getTime();

    // Calculează diferența dintre data setată și data curentă
    var distance = countDownDate - now;

    // Calcularea zilelor, orelor, minutelor și secundelor
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);

    // Afișează rezultatul în elementul cu id-ul "date-countdown"
    document.getElementById("date-countdown").innerHTML = days + "z " + hours + "h "
        + minutes + "m " + seconds + "s ";

    // Dacă countdown-ul a expirat
    if (distance < 0) {
        clearInterval(x);
        document.getElementById("date-countdown").innerHTML = "Evenimentul a început!";
    }
}, 1000);
